import React,{useState,useEffect} from "react";
import { Link } from "react-router-dom";
import SideBar from "./Sidebar";
import NavBar from "./Navbar";
function Class(){

    const [data, setData] = useState([])
	const [refresh, setRefresh] = useState(0)

	useEffect(() => {
		fetch('http://localhost:4001/studentview').then((res) =>
			res.json()
		).then((result) => 
		setData(result)
		// console.log(result)
		)		
	}, [refresh])

    return(
        <>
         <div class="content">
       <SideBar></SideBar>
       <NavBar></NavBar>
       <div class="col-sm-12 col-xl-6">
                        <div class="bg-light rounded h-100 p-4">
                            <h6 class="mb-4">CLASS 1</h6>
                            <table class="table table-hover">
                                <thead>
                                    <tr>
                                        <th scope="col">SI NO</th>
                                        <th scope="col">STUDENTS</th>
                                        <th scope="col">ROLL NO</th>
                                        
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.map((value,index)=>{
                                        return(
                                            <>
                                            <tr key={value.Student_id}>
                                            <th scope="row">1</th>
                                            <td>{value.Name}</td>
                                            <td></td>
                                          
                                        </tr>
                                        <tr>
                                            <th scope="row">2</th>
                                            <td>{value.R}</td>
                                            <td></td>
                                           
                                        </tr>
                                        <tr>
                                            <th scope="row">3</th>
                                            <td></td>
                                            <td></td>
                                            
                                        </tr>
                                        </>
                                        )
                                    })}
                                 
                                </tbody>
                            </table>
                        </div>
                        
                    </div>
                    <button type="button" class="btn btn-primary m-2">ADD STUDENTS</button>
                    <Link to="/addstudent"  style={{ marginLeft: '160px' }}>View</Link>
                    </div>
                   
                   
        </>
    )
}
export default Class
