import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import NavBar from "./Navbar";
import SideBar from "./Sidebar";

const ExamView = () => {
  const [examData, setExamData] = useState([]);

  useEffect(() => {
    // Fetch exam data from the backend
    fetch(`http://localhost:4002/api/examview/`)
      .then((response) => response.json())
      .then((data) => {
        setExamData(data);
        console.log("Fetched Exam Data:", data);
      })
      .catch((error) => console.error("Error fetching exam data:", error));
  }, []);

  return (
    <div className="content">
<NavBar/>
<SideBar/>
    <div>
      <h1>Exam Details</h1>
      <table border="1"  class="table table-hover">
        <thead>
          <tr>
           
            <th>Class</th>
            <th>Title</th>
            <th>Questions</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {examData.map((item, index) => (
            <tr key={index}>
             
              <td>{item.class}</td>
              <td>{item.title}</td>
              <td><button style={{marginLeft:'20px',borderRadius:'20px'}}> üëÅÔ∏è‚Äçüó®Ô∏è</button></td>
              {/* <td>
                <ul>
                  {item.questions.map((question, qIndex) => (
                    <li key={qIndex}>{question}</li>
                  ))}
                </ul>
              </td> */}
              <td>S</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
};

export default ExamView;
