import React, { useState, useEffect } from "react";
import NavBar from "./Navbar";

function StudentActive() {
  const [activeExam, setActiveExam] = useState(null);

  useEffect(() => {
    fetch("http://localhost:4002/api/examview/")
      .then((res) => res.json())
      .then((result) => {
        // Filter active exams based on the current time
        const currentTime = new Date().getTime();
        const activeExams = result.filter(
          (exam) =>
            currentTime >= new Date(exam.startTime).getTime() &&
            currentTime <= new Date(exam.endTime).getTime()
        );

        if (activeExams.length > 0) {
          // If there are active exams, select the first one
          setActiveExam(activeExams[0]);
        } else {
          // If there are no active exams, set activeExam to null
          setActiveExam(null);
        }
      })
      .catch((error) => console.error("Error fetching active exams:", error));
  }, []);

  return (
    <>
      <NavBar />
      <div className="container mt-4">
        <h4 className="mb-4 text-center">Active Exams</h4>
        {activeExam ? (
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Exam Name</th>
                <th>Start Time</th>
                <th>End Time</th>
              </tr>
            </thead>
            <tbody>
              <tr key={activeExam.examId}>
                <td>{activeExam.examName}</td>
                <td>{new Date(activeExam.startTime).toLocaleString()}</td>
                <td>{new Date(activeExam.endTime).toLocaleString()}</td>
              </tr>
            </tbody>
          </table>
        ) : (
          <p className="text-center">No active exams at the moment.</p>
        )}
      </div>
    </>
  );
}

export default StudentActive;
