import React ,{useState,}from "react";
import SideBar from "./Sidebar";
import NavBar from "./Navbar";
function AddStudent(){
const [name,setName]=useState('');
const [roll,setRoll]=useState('');
const [admsn,setAdmsn]=useState('');

const handleForm=(e)=>{
    e.preventDefault();	
    let formData = new FormData();
    formData.append('name', name);
    formData.append('roll', roll);
    formData.append('admsn', admsn);
}
    return(
        <>
         <div class="content">
            <SideBar></SideBar>
            <NavBar></NavBar>
            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: '50vh', padding: '20px' }}>
    <h3>Student details</h3>
    <form method="post" onSubmit={handleForm} style={{ width: '360px', textAlign: 'left', border: '2px solid ', padding: '20px' }}>

            <label style={{ display: "inline-block",width: "100px" }}>Name</label>
            <input type="text" name="name" onChange={(e) => setName(e.target.value)}/> <br /><br />

            <label style={{ display: "inline-block",width: "100px" }}>Rollnumber</label>
            <input type="text" name="roll" onChange={(e) => setRoll(e.target.value)}/> <br /><br />

            <label style={{ display: "inline-block",width: "100px" }}>Admission No</label>
            <input type="text" name="admsn" onChange={(e) => setAdmsn(e.target.value)} /> <br /><br />
           
            <button type="submit" style={{ backgroundColor: '#fff', color: '#222',marginLeft:'150px' }}>Submit</button><br />
           
        </form>

        </div>
         </div>
        </>
    )
}
export default AddStudent
