import React ,{useState,useEffect}from 'react'

function ResultFull() {
    const [data, setData] = useState([])
	const [refresh, setRefresh] = useState(0)

	useEffect(() => {
		fetch('http://localhost:4002/answerview').then((res) =>
			res.json()
		).then((result) => 
		setData(result)
		// console.log(result)
		)		
	}, [refresh])
  return (
    <div>
        
     <h6 class="mb-4 text-center "><u>STUDENT'S DETAILS</u></h6>
                            <table class="table table-hover">
                                <thead>
                                    <tr>
                                        {/* <th scope="col">SI NO</th> */}
                                        <th scope="col-3">Student</th>
                                        <th scope="col">marks</th>
                                        {/* <th scope="col">Admsn no</th>
                                        <th colSpan={2}>Action</th> */}
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.map((value,index)=>{
                                        
                                        return(
                                            <>
                                            <tr>
                                           
                                            {/* <th scope="row"></th> */}
                                            
                                            <td>{value.username}</td>
                                            <td>{value.marks}</td>
                                            {/* <td>{value.Admsn}</td> */}
                                            
                                            
                                        
                                        {/* <td><button class="btn btn-info m-2"style={{ color: '#222',marginLeft:'150px' }}onClick={() => handleDelete(value._id)}>Delete</button></td> */}
                                       
                                       
                                        </tr>
                                   
                                        </>
                                        
                                        )
                                    })}
                                  
                                </tbody>
                            </table>
    </div>
  )
}

export default ResultFull
