import React, { useState, useEffect } from "react";
import NavBar from "./Navbar";
import SideBar from "./Sidebar";

const ExamView = () => {
  // ... (existing code)

  const handleScheduleExam = (index) => {
    const selectedExam = examData[index];
    setScheduleExamData({
      class: selectedExam.class,
      title: selectedExam.title,
      scheduledDate: "",
      scheduledstartTime: "",
      scheduledendTime: "",
    });
    setIsScheduling(true);
  };

  const handleScheduleSubmit = (e) => {
    e.preventDefault();

    const { class: examClass, title, scheduledDate, scheduledstartTime, scheduledendTime } = scheduleExamData;

    // Send the scheduled exam data to the backend
    fetch(`http://localhost:4002/api/scheduleexam/${selectedExam._id}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        scheduledDate,
        scheduledstartTime,
        scheduledendTime,
      }),
    })
    .then((response) => response.json())
    .then((data) => {
      alert(data.message);
    })
    .catch((error) => console.error("Error scheduling exam:", error));

    // Reset form data
    setScheduleExamData({
      class: "",
      title: "",
      scheduledDate: "",
      scheduledstartTime: "",
      scheduledendTime: "",
    });

    setIsScheduling(false);
  };

  // ... (existing code)

  return (
    <div className="content">
      <NavBar />
      <SideBar />
      <div>
        {/* ... (existing code) */}
        {isScheduling && (
          <div>
            <h2 className="text-center mb-4">SCHEDULE EXAM</h2>
            <form onSubmit={handleScheduleSubmit}>
              {/* ... (existing code) */}
            </form>
          </div>
        )}
      </div>
    </div>
  );
};

export default ExamView;
