import React,{useState,useEffect} from "react";
import NavBar from "./Navbar";
import SideBar from "./Sidebar";
import { Link } from "react-router-dom";
function TeacherView(){
    const [data, setData] = useState([])
	const [refresh, setRefresh] = useState(0)

	useEffect(() => {
		fetch('http://localhost:4002/teacherview').then((res) =>
			res.json()
		).then((result) => 
		setData(result)
		// console.log(result)
		)		
	}, [refresh])
    
    const handleDelete = (iD)=>{
		let params = {
			id:iD
		}
		fetch('http://localhost:4002/teacherdelete',{
			method:'post',
			headers:{
				Accept :'application/json',
				'Content-Type':'application/json'
			},
			body:JSON.stringify(params)
		}).then((res)=>res.json()).then((result)=>{
			setRefresh((previous)=>previous+1)
		})
	}
	
    return(


	
        <>
      <div class="content">

        <NavBar></NavBar>
        <SideBar/>

       
		<div style={{ display: 'flex', flexDirection: 'column', marginLeft:'50px', alignItems: 'center', justifyContent: 'center', minHeight: '10vh', padding: '20px' }}>
       <div class="col-sm-12 col-xl-6">
                        {/* <div class="bg-light rounded h-100 p-4 col-md-12"> */}
                            <h6 class="mb-4 text-center">TEACHER'S DETAILS</h6>
                            <table class="table table-hover"border={2}>
                                <thead>
                                    <tr>
                                        {/* <th scope="col">SI NO</th> */}
                                        <th scope="col-3">Teachername</th>
                                        <th scope="col">subject</th>
                                        <th scope="col">email</th>
                                        <th scope="col">phno</th>
                                        <th scope="col">address</th>
                                        <th scope="col">image</th>
                                        <th colSpan={3}>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.map((value,index)=>{
                                        
                                        return(
                                            <>
                                            <tr>
                                           
                                            {/* <th scope="row"></th> */}
                                            
                                            <td>{value.Teachername}</td>
                                            <td>{value.Subject}</td>
                                            <td>{value.Email}</td>
                                            <td>{value.Phno}</td>
                                            <td>{value.Address}</td>
                                            <td>{value.Image}</td>
                                            
                                        <button class="btn btn-primary m-2"onClick={() => handleDelete(value._id)}>Delete</button>
                                        <td><Link to="/teacedit" state={{id:value._id}}>Edit</Link></td>
                                        </tr>
                                   
                                        </>
                                        
                                        )
                                    })}
                                  
                                </tbody>
                            </table>
                        {/* </div> */}
                       
                    </div>
                   
                   
                    
                    </div>
      </div>
        </>
    )
}
export default TeacherView
