import React, { useState } from "react";

function YourComponent() {
  const [classes, setClasses] = useState(["Class1"]);

  const addClass = () => {
    const newClass = `Class${classes.length + 1}`;
    setClasses([...classes, newClass]);
  };

  return (
    <div className="col-sm-12 col-xl-12 alert alert-success">
      <div className="bg-light rounded h-100 p-3">
        <span className="add-class-symbol" style={{ border: '2px solid green', borderRadius: '150px', marginTop: '80px', padding: '8px' }} onClick={addClass}>
          +
        </span>
        <h4 className="mb-4 text-center"> My Classes</h4>
        <div className="d-flex flex-wrap">
          {classes.map((className, index) => (
            <div key={index} className="col-sm-12 col-xl-3 alert alert-danger mr-3">
              <div className="bg-light rounded h-100 p-3">
                <a href={`/class/${className}`} className="stretched-link">
                  <p>{className}</p>
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default YourComponent;
