// ExamTest.js
import React, { useState } from "react";
import NavBar from "./Navbar";

const ExamTest = ({ title, questions, onSubmit }) => {
  const [classValue, setClassValue] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    // Additional logic or sending data to backend can be done here
    onSubmit({ title, class: classValue, questions });
  };

  return (
    <>
      <NavBar />
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Title
          </label>
          <input
            type="text"
            name="title"
            className="form-control"
            defaultValue={title}
            readOnly
          />
        </div>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Class
          </label>
          <input
            type="text"
            name="class"
            className="form-control"
            value={classValue}
            onChange={(e) => setClassValue(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Questions
          </label>
          <textarea
            name="questions"
            className="form-control"
            value={questions}
            readOnly
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </>
  );
};

export default ExamTest;
