import React, { useState, useEffect } from "react";
import NavBar from "./Navbar";
import SideBar from "./Sidebar";

const ExamView = () => {
  const [examData, setExamData] = useState([]);
  const [scheduleExamData, setScheduleExamData] = useState({
    class: "",
    title: "",
    scheduledDate: "",
    scheduledTime: "",
  });
  const [isScheduling, setIsScheduling] = useState(false);

  useEffect(() => {
    // Fetch exam data from the backend
    fetch(`http://localhost:4002/api/examview/`)
      .then((response) => response.json())
      .then((data) => {
        setExamData(data);
        console.log("Fetched Exam Data:", data);
      })
      .catch((error) => console.error("Error fetching exam data:", error));
  }, []);

  const handleScheduleExam = (index) => {
    const selectedExam = examData[index];
    setScheduleExamData({
      class: selectedExam.class,
      title: selectedExam.title,
      scheduledDate: "",
      scheduledTime: "",
    });
    setIsScheduling(true);
  };

  const handleScheduleSubmit = (e) => {
    e.preventDefault();

    // Implement the logic to send the scheduled exam data to the backend
    // ...

    // Reset form data
    setScheduleExamData({
      class: "",
      title: "",
      scheduledDate: "",
      scheduledTime: "",
    });

    setIsScheduling(false);

    // Show alert or confirmation modal
    alert("Exam scheduled successfully!");
  };

  return (
    <div className="content">
      <NavBar />
      <SideBar />
      <div>
        <h1 className="text-center">·¥áx·¥Ä·¥ç ·¥Ö·¥á·¥õ·¥Ä…™ üÍú±</h1>
        <table border="1" className="table table-hover">
          <thead>
            <tr>
              <th>Class</th>
              <th>Title</th>
              <th>Questions</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {examData.map((item, index) => (
              <tr key={index}>
                <td>{item.class}</td>
                <td>{item.title}</td>
                <td>
                  <button style={{ marginLeft: '20px', borderRadius: '20px' }}> üëÅÔ∏è‚Äçüó®Ô∏è</button>
                </td>
                <td>
                  <button className="btn btn-info m-2" onClick={() => handleScheduleExam(index)}>
                    Schedule Exam
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {isScheduling && (
          // <div>
          //   <h2>SCHEDULE EXAM</h2>
          //   <form onSubmit={handleScheduleSubmit}>
          //     <div>
          //       <label>Class:</label>
          //       <input type="text" value={scheduleExamData.class} disabled />
          //     </div>
          //     <div>
          //       <label>Title:</label>
          //       <input type="text" value={scheduleExamData.title} disabled />
          //     </div>
          //     <div>
          //       <label>Scheduled Date:</label>
          //       <input
          //         type="date"
          //         value={scheduleExamData.scheduledDate}
          //         onChange={(e) => setScheduleExamData({ ...scheduleExamData, scheduledDate: e.target.value })}
          //         required
          //       />
          //     </div>
          //     <div>
          //       <label>Scheduled Time:</label>
          //       <input
          //         type="time"
          //         value={scheduleExamData.scheduledTime}
          //         onChange={(e) => setScheduleExamData({ ...scheduleExamData, scheduledTime: e.target.value })}
          //         required
          //       />
          //     </div>
          //     <button type="submit">Schedule</button>
          //   </form>
          // </div>
          <div>
  <h2 className="text-center mb-4">SCHEDULE E</h2>
  <form onSubmit={handleScheduleSubmit}>
    <div className="form-group">
      <label>Class:</label>
      <input type="text" value={scheduleExamData.class} className="form-control" disabled />
    </div>
    <div className="form-group">
      <label>Title:</label>
      <input type="text" value={scheduleExamData.title} className="form-control" disabled />
    </div>
    <div className="form-group">
      <label>Scheduled Date:</label>
      <input
        type="date"
        value={scheduleExamData.scheduledDate}
        onChange={(e) => setScheduleExamData({ ...scheduleExamData, scheduledDate: e.target.value })}
        className="form-control"
        required
      />
    </div>
    <div className="form-group">
      <label>Scheduled Time:</label>
      <input
        type="time"
        value={scheduleExamData.scheduledTime}
        onChange={(e) => setScheduleExamData({ ...scheduleExamData, scheduledTime: e.target.value })}
        className="form-control"
        required
      />
    </div>
    <button type="submit" className="btn btn-primary">Schedule</button>
  </form>
</div>

        )}
      </div>
