import React, { useState, useEffect } from "react";
import NavBar from "./Navbar";

function Studentcompleted() {
  const [completedExams, setCompletedExams] = useState([]);

  useEffect(() => {
    // Fetch all exams data from your API or data source
    // Replace the following with your actual endpoint or data fetching logic
    fetch('http://localhost:4002/api/examview/')
      .then((res) => res.json())
      .then((result) => {
        // Filter completed exams based on current date and time
        const currentDate = new Date();

        const filteredCompletedExams = result.filter((exam) => {
          const examEndDateTime = new Date(`${exam.scheduledDate} ${exam.scheduledendTime}`);

          return currentDate > examEndDateTime;
        });

        setCompletedExams(filteredCompletedExams);
      });
  }, []);


  const handleStartExam = (index) => {
    const selectedExam = examData[index];
    const currentTime = new Date();
    console.log(selectedExam)
    const startTime = new Date(`${selectedExam.scheduledDate}T${selectedExam.scheduledstartTime}`);
    const endTime = new Date(`${selectedExam.scheduledDate}T${selectedExam.scheduledendTime}`);

    if (currentTime >= startTime && currentTime <= endTime) {
      setSelectedExam(selectedExam);
      setIsScheduling(true);
     
      
      navigate('/examtest', { state: { examId: selectedExam._id } });
    } else {
      alert("The exam has not started yet or has already ended.");
    }
  };
  return (
    <>
      <NavBar />
      <div className="container mt-4">
        <h4 className="mb-4 text-center">Completed Exams</h4>
        <table className="table table-bordered  table table-hover alert alert-danger">
          <thead>
            <tr>
              <th>Exam Name</th>
              <th>Exam Date</th>
              <th>Start Time</th>
              <th>End Time</th>
            </tr>
          </thead>
          <tbody>
            {completedExams.map((exam) => (
              <tr key={exam._id}>
                <td>{exam.title}</td>
                <td>{exam.scheduledDate}</td>
                <td>{exam.scheduledstartTime}</td>
                <td>{exam.scheduledendTime}</td>
                <td>
                  <button
                    className="btn btn-success m-2"
                    onClick={() => handleStartExam(index)}
                  >
                    START
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default Studentcompleted;
