import React, { useState,useEffect } from "react";
import NavBar from "./Navbar";
import SideBar from "./Sidebar";
function StudentEdit(){
    const [name, setName] = useState('')
    const [rollno, setRollno] = useState('')
    const [admsno, setAdmsnno] = useState('')
    const[image,setImage]=useState('')
    return(
        <>
     <div class="content">
            <NavBar/>
            <SideBar/>
            <div style={{ display: 'flex', flexDirection: 'column', marginLeft:'300px', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', padding: '20px' }}>
        <div class="container-fluid pt-4 px-4 ">
                <div class="row g-4 ">
                    
                    
                <div class="col-sm-12 col-xl-6 alert alert-primary">
                        <div class="bg-light rounded h-100 p-4">
                            <h4 class="mb-4 text-center"> Profile</h4>
                                {/* <div className="text-center">
                                <img className="rounded-circle " src="img/user.jpg" alt="" style={{ width: '100px', height: '100px' }} />
                                </div><br></br> */}
                                {/* <div className="text-center">
    {filterdata.map((value, index) => (
    <img
      key={index} 
      src={value.Image} 
      alt={`Profile of ${value.Teachername}`}
      className="rounded-circle"
      style={{ width: '100px', height: '100px' }}
    />
  ))}
</div> */}
                        
                <form action="" method="post" onSubmit={handleUpdate}>
                <div className="mb-3 ">
                  <label htmlFor="exampleInputEmail1" className="form-label"> Name</label>
                  <input type="text"  name='teachername'className="form-control" onChange={(e)=>setName(e.target.value)} value={teachername}id="exampleInputEmail1" aria-describedby="emailHelp" />
                </div>
                <div className="mb-3">
                  <label htmlFor="exampleInputtext" className="form-label">Email</label>
                  <input type="email" name='emai'className="form-control"onChange={(e)=>setRollno(e.target.value)} id="exampleInputtext" />
                </div>
                <div className="mb-3">
                  <label htmlFor="exampleInputtext" className="form-label">Subject</label>
                  <input type="text" name='subject'className="form-control" onChange={(e)=>setAdmsnno(e.target.value)} value={subject}id="exampleInputtext" />
                </div>
               
               
                <div className="mb-3">
                  <label htmlFor="exampleInputtext" className="form-label">Image</label>
                  <input type="file" name='image'className="form-control" onChange={(e)=>{setImage(e.target.files[0]);setImageView(URL.createObjectURL(e.target.files[0]))}}id="exampleInputtext" />
                  {/* setImageView(URL.createObjectURL(e.target.files[0]));}} */}
			<img src={imageView} alt="Hii" width="100" height="100" />
                </div>

                <button type="submit" className="btn btn-primary">Update</button>
                
              </form>

                        </div>
                    </div>




                </div>

        </div>


</div>

                               
            </div>
        </>
    )
}
export default StudentEdit
