// StudentExam.js
import React, { useState, useEffect } from "react";
import NavBar from "./Navbar";
import { Link, useNavigate } from "react-router-dom";
import ExamTest from "./ExamTest";

const StudentExam = () => {
  const [examData, setExamData] = useState([]);
  const [selectedExam, setSelectedExam] = useState(null);
  const [isScheduling, setIsScheduling] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch exam data from the backend
    fetch(`http://localhost:4002/api/examview/`)
      .then((response) => response.json())
      .then((data) => {
        setExamData(data);
        console.log("Fetched Exam Data:", data);
      })
      .catch((error) => console.error("Error fetching exam data:", error));
  }, []);

  const handleStartExam = (index) => {
    const selectedExam = examData[index];
    setSelectedExam(selectedExam);
    setIsScheduling(true);
    // Navigate to the ExamTest page
    navigate('/examtest');
  };

  return (
    <>
      <NavBar />
      <div>
        <h1 className="text-center">·¥áx·¥Ä·¥ç ·¥Ö·¥á·¥õ·¥Ä…™ üÍú±</h1>
        <table border="1" className="table table-hover">
          {/* ... (other table headers) */}
          <thead>
            <tr>
              <th>Class</th>
              <th>Title</th>
              <th>Questions</th>
              <th>Date</th>
              <th>Start Time</th>
              <th>End time</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {examData.map((item, index) => (
              <tr key={index}>
                <td>{item.class}</td>
                <td>{item.title}</td>
                <td>
                  <ul>
                    {item.questions.map((question, qIndex) => (
                      <li key={qIndex}>{question}</li>
                    ))}
                  </ul>
                </td>
                <td>{item.scheduledDate}</td>
                <td>{item.scheduledstartTime}</td>
                <td>{item.scheduledendTime}</td>
                <td>
                  <button
                    className="btn btn-success m-2"
                    onClick={() => handleStartExam(index)}
                  >
                    START
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Display ExamTest component only when isScheduling is true */}
      {isScheduling && (
        <ExamTest
          title={selectedExam.title}
          class={selectedExam.class}
          questions={selectedExam.questions}
        />
      )}
    </>
  );
};

export default StudentExam;
