import React,{useState,useEffect} from "react";
import NavBar from "./Navbar";
import SideBar from "./Sidebar";
function TeacherView(){
    const [data, setData] = useState([])
	const [refresh, setRefresh] = useState(0)

	useEffect(() => {
		fetch('http://localhost:4002/teacherview').then((res) =>
			res.json()
		).then((result) => 
		setData(result)
		// console.log(result)
		)		
	}, [refresh])

    return(


	
        <>
      <div class="content">

        <NavBar></NavBar>
        <SideBar/>

        <h3>Student details</h3>
		<div style={{ display: 'flex', flexDirection: 'column', marginLeft:'50px', alignItems: 'center', justifyContent: 'center', minHeight: '10vh', padding: '20px' }}>
       <div class="col-sm-12 col-xl-6">
                        {/* <div class="bg-light rounded h-100 p-4 col-md-12"> */}
                            <h6 class="mb-4 text-center">TEACHER'S DETAILS</h6>
                            <table class="table table "border={2}>
                                <thead>
                                    <tr>
                                        {/* <th scope="col">SI NO</th> */}
                                        <th scope="col-3">Teachername</th>
                                        <th scope="col">subject</th>
                                        <th scope="col">email</th>
                                        <th scope="col">phno</th>
                                        <th scope="col">address</th>
                                        <th scope="col">image</th>
                                        <th colSpan={3}>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data.map((value,index)=>{
                                        
                                        return(
                                            <>
                                            <tr>
                                           
                                            {/* <th scope="row"></th> */}
                                            
                                            <td>{value.Teachername}</td>
                                            <td>{value.Subject}</td>
                                            <td>{value.Email}</td>
                                            <td>{value.Phno}</td>
                                            <td>{value.Address}</td>
                                            <td>{value.Image}</td>
                                            
                                        {/* <button class="btn btn-primary m-2"onClick={() => handleDelete(value._id)}>Delete</button></td> */}
                                        <button class="btn btn-primary m-2">Delete</button>
                                        </tr>
                                   
                                        </>
                                        
                                        )
                                    })}
                                  
                                </tbody>
                            </table>
                        {/* </div> */}
                       
                    </div>
                   
                    {/* <Link to="/addstudent"  style={{ marginLeft: '20px',border:'2px solid',borderRadius:'20px',padding:'8px'}}>ADD STUDENT</Link> */}
                    
                    
                    </div>
                    
      </div>
        </>
    )
}
export default TeacherView
