import React, { useState, useEffect } from "react";

function PreviousDisplay(props) {
  const [examDetails, setExamDetails] = useState(null);

  useEffect(() => {
    const examId = props.match.params.examId; // Assuming you pass examId through the route

    fetch(`http://localhost:4002/api/previousexam/${examId}`)
      .then((res) => res.json())
      .then((data) => {
        setExamDetails(data);
      })
      .catch((error) => {
        console.error("Error fetching exam details:", error);
      });
  }, [props.match.params.examId]);

  return (
    <>
      <h1>Exam Details</h1>
      {examDetails ? (
        <div>
          <p>Exam ID: {examDetails._id}</p>
          <p>Exam Name: {examDetails.title}</p>
          <p>Exam Date: {examDetails.scheduledDate}</p>
          <p>Start Time: {examDetails.scheduledstartTime}</p>
          <p>End Time: {examDetails.scheduledendTime}</p>
          {/* Add logic to display questions and answers */}
          {examDetails.answers.map((question) => (
            <div key={question.id}>
              <p>Question: {question.text}</p>
              <p>Answer: {question.answer}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>Loading exam details...</p>
      )}
    </>
  );
}

export default PreviousDisplay;
