// export default ExamResult
import React, { useState, useEffect } from "react";

function ExamResult() {
  const [marks, setMarks] = useState(0);
  const username = localStorage.getItem("username");

  useEffect(() => {
    // Fetch marks data from the backend API
    fetch("http://localhost:4002/answerview", {
      method: "post",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ username: username }), // Use username instead of id
    })
      .then((res) => res.json())
      .then((result) => {
        setMarks(result.marks);
      })
      .catch((error) => {
        console.error("Error fetching marks:", error);
      });
  }, [username]);
  return (
    <>
      <h1 className="text-center">Exam Result</h1>
      <div className="mb-3">
        <p className="text-center">Name: {username}</p>
        <p className="text-center">Marks: {marks}</p>
      </div>
    </>
  );
}

export default ExamResult;



