import React from "react";
import NavBar from "./Navbar";
function AdminSignup(){
  const [tearname, setTeachername] = useState('')
  const [subject, setSubject] = useState('')



const handleSubmit = (e)=>{
    e.preventDefault()
    let params = {
        teachername:teachername,
        subject:subject,
       
    }
    
    fetch('http://localhost:4000/addteacher',{
        method:'post',
        headers:{
            Accept:'application/json',
            'Content-Type':'application/json'
        },
        body:JSON.stringify(params)
    }).then((res)=>res.json()).then((result)=>{
        console.log(result);
    })
}
    return(
        <>
        {/* <h1>hiiii login</h1> */}
        <NavBar></NavBar>
        {/* <div style={{backgroundImage:'url(img/school.jpeg)',width:'400px',height:'500px',backgroundSize:'cover'}}></div> */}

    <div className="container-fluid">
      <div className="row h-100 align-items-center justify-content-center" style={{ minHeight: '100vh' }}>
        <div className="col-12 col-sm-8 col-md-6 col-lg-5 col-xl-4">
          <div className="bg-light rounded p-4 p-sm-5 my-4 mx-3">
            <div className="d-flex align-items-center justify-content-between mb-3">
              <a href="index.html" className="">
                <h3 className="text-primary"><i className="fa fa-hashtag me-2"></i>Admin</h3>
              </a>
              <h3>Sign Up</h3>
            </div>
            <div className="form-floating mb-3">
              <input type="text" className="form-control" id="floatingText" placeholder="jhondoe" />
              <label htmlFor="floatingText">Username</label>
            </div>
            <div className="form-floating mb-3">
              <input type="email" className="form-control" id="floatingInput" placeholder="name@example.com" />
              <label htmlFor="floatingInput">Email address</label>
            </div>
            <div className="form-floating mb-4">
              <input type="password" className="form-control" id="floatingPassword" placeholder="Password" />
              <label htmlFor="floatingPassword">Password</label>
            </div>
            <div className="d-flex align-items-center justify-content-between mb-4">
              <div className="form-check">
                <input type="checkbox" className="form-check-input" id="exampleCheck1" />
                <label className="form-check-label" htmlFor="exampleCheck1">Check me out</label>
              </div>
              <a href="">Forgot Password</a>
            </div>
            <button type="submit" className="btn btn-primary py-3 w-100 mb-4"><a href="/adminhome">Sign Up</a></button>
            <p className="text-center mb-0">Already have an Account? <a href="/adminlogin">Sign In</a></p>
          </div>
        </div>
      </div>
    </div>
 

        </>
    )
}
export default AdminSignup
