import React, { useState, useEffect } from "react";
import SideBar from "./Sidebar";
import NavBar from "./Navbar";

function TeacherHome() {
  const username = localStorage.getItem('username');
  const [data, setData] = useState([]);
  // const filterdata = data.filter((value) => value.Teachername === username);
  // console.log(filterdata)
  const [classes, setClasses] = useState(() => {
    // const storedClasses = localStorage.getItem('classes');
    // return storedClasses ? JSON.parse(storedClasses) : ["Class1"];
  });

  const addClass = () => {
    const newClass = `Class${classes.length + 1}`;
    setClasses((prevClasses) => [...prevClasses, newClass]);
  };

  useEffect(() => {
    localStorage.setItem('classes', JSON.stringify(classes));
  }, [classes]);
  useEffect(() => {
    fetch('http://localhost:4002/teacherview').then((res) =>
      res.json()
    ).then((result) => 
    setData(result)
    // console.log(result)
    )		
  }, []);
   const filterdata=data.filter((value)=>value.
   Teachername===username)
   console.log(filterdata)
  return (
    <>
      <div className="content">
        <SideBar />
        <NavBar />
        <div className="container-fluid pt-4 px-4">
          
          <div class="row g-4 ">
                    <div class="col-sm-12 col-xl-6 alert alert-primary  ">
                        <div class="bg-light rounded h-100 p-4">
                            <h4 class="mb-4 text-center">𝐏𝐑𝐎𝐅𝐈𝐋𝐄</h4>
                                <div className="text-center">
                                  
                                <img
              className="rounded-circle me-lg-2"
              src={filterdata[0]?.Image ? `http://localhost:4002/${filterdata[0].Image}` : "img/user.jpeg"}
              alt=""
              style={{ width: "40px", height: "40px" }}
            />
                                {/* <img className="rounded-circle " src="img/user.jpeg" alt="hbb" style={{ width: '100px', height: '100px' }} /> */}
                                </div><br></br>


                            <p class=" text-center">Name:{username}</p>
                            <p class=" text-center">Subject:Maths</p>
                            <a href="/teacherprofile" class=" stretched-link"> </a>
                        </div>
                    </div>
                    <div class="col-sm-12 col-xl-6 alert alert-secondary ">
                        <div class="bg-light rounded h-100 p-4">
                             <h4 class="mb-4 text-center ">𝗘𝗫𝗔𝗠 𝗛𝗜𝗦𝗧𝗢𝗥𝗬</h4>
                               <div class="col-sm-12 col-xl-12 alert alert-warning">
                            <p>1.TestPaper 1</p>
                               </div>
                                  <div class="col-sm-12 col-xl-12 alert alert-warning">
                                    <p>2.TestPaper 2</p>
                                      </div>
                                        </div>
                                         </div>

          <div className="col-sm-12 col-xl-12 alert alert-success">
            <div className="bg-light rounded h-100 p-3">
              <span
                className="add-class-symbol"
                style={{ border: '2px solid green', borderRadius: '150px', marginTop: '80px', padding: '8px' }}
                onClick={addClass}
              >
