import React, { useState, useEffect } from "react";

function PreviousDisplay(props) {
  const { examDetails } = props.location.state;

  return (
    <>
      <h1>Exam Details</h1>
      {examDetails ? (
        <div>
          <p>Exam ID: {examDetails._id}</p>
          <p>Exam Name: {examDetails.title}</p>
          <p>Exam Date: {examDetails.scheduledDate}</p>
          <p>Start Time: {examDetails.scheduledstartTime}</p>
          <p>End Time: {examDetails.scheduledendTime}</p>
          {/* Add logic to display questions and answers */}
          {examDetails.answers.map((question) => (
            <div key={question.id}>
              <p>Question: {question.text}</p>
              <p>Answer: {question.answer}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>Loading exam details...</p>
      )}
    </>
  );
}

export default PreviousDisplay;
