import React,{useState,useEffect} from "react";
import NavBar from "./Navbar";
function TeacherView(){
    const [data, setData] = useState([])
	const [refresh, setRefresh] = useState(0)

	useEffect(() => {
		fetch('http://localhost:4002/teacherview').then((res) =>
			res.json()
		).then((result) => 
		setData(result)
		// console.log(result)
		)		
	}, [refresh])

    return(


	
        <>
      <div class="content">

        <NavBar></NavBar>

        <h3>Student details</h3>
		<table className="styled-table" border={2}>
				<thead>
					<tr>
						<th>TeachernameName</th>
						<th>Subject</th>
                        <th>email</th>
                        <th>Image</th>
                        <th>Image</th>
						<th colSpan={3}>Action</th>
					</tr>
				</thead>
				<tbody >
					{data.map((value, index) => {
						// console.log(value);
						return (
							<tr key={value.Student_id}>
								<td>{value.Name}</td>
								<td><img src={value.Image}  width="100" height="100" /></td>
								{/* <td><Link to="/edit" state={{id:value._id}}>Edit</Link></td> */}
								{/* <td><button onClick={()=>handleDelete(value._id)}>delete</button></td> */}
								{/* <td><Link to="/viewinfo" state={{id:value._id}} style={{ marginLeft: '25px' }}>View</Link></td> */}
							</tr>
						)
					})}

				</tbody>
			</table>
      </div>
        </>
    )
}
export default TeacherView
