import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import NavBar from "./Navbar";

function ExamTest() {
  const location = useLocation();
  const [examData, setExamData] = useState([]);
  const [title, setTitle] = useState("");
  const [classs, setClasss] = useState("");
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState(Array.from({ length: 10 }, () => ""));
  const [remainingTime, setRemainingTime] = useState(60 * 60); // Initial time in seconds, e.g., 1 hour

  useEffect(() => {
    let params = {
      id: location.state.examId,
    };

    fetch("http://localhost:4002/examtest", {
      method: "post",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(params),
    })
      .then((res) => res.json())
      .then((result) => {
        setTitle(result.title);
        setClasss(result.class);
        setQuestions(result.questions);
      });
  }, [location.state.examId]);

  useEffect(() => {
    const timerInterval = setInterval(() => {
      setRemainingTime((prevTime) => (prevTime > 0 ? prevTime - 1 : 0));
    }, 1000);

    // Cleanup the interval on component unmount
    return () => clearInterval(timerInterval);
  }, []);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${String(minutes).padStart(2, "0")}:${String(remainingSeconds).padStart(2, "0")}`;
  };

  return (
    <>
      <NavBar />
      <div className="timer">Remaining Time: {formatTime(remainingTime)}</div>
      <form action="" method="post">
        {/* ... rest of your form code */}
      </form>
    </>
  );
}

export default ExamTest;
