import React, { useState } from "react";
import SideBar from "./Sidebar";
import NavBar from "./Navbar";

function AddExam() {
  const [showQuestions, setShowQuestions] = useState(false);
  const [questions, setQuestions] = useState([]);

  const handleSubmit = (event) => {
    // Handle form submission logic here
    event.preventDefault();
    // Add your logic to handle form submission
  };

  const handleAddQuestions = () => {
    setShowQuestions(!showQuestions);
    if (!showQuestions) {
      // Add initial set of questions when "Add Questions" is clicked
      setQuestions(["Question 1", "Question 2", "Question 3"]);
    }
  };

  return (
    <>
      <div className="content">
        <SideBar />
        <NavBar />
        <div style={{ display: 'flex', flexDirection: 'column', marginLeft:'300px', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', padding: '20px' }}>
          <div className="container-fluid pt-4 px-4 ">
            <div className="row g-4 ">
              <div className="col-sm-12 col-xl-6 alert alert-primary">
                <div className="bg-light rounded h-100 p-4">
                  <h4 className="mb-4 text-center">Exam Details</h4>
                  <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                      <label htmlFor="exampleInputEmail1" className="form-label">Title:</label>
                      <input type="text" name="title" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="exampleInputEmail1" className="form-label">Class</label><br></br>
                      <select name="class" className="form-control" style={{ width: '350px' }}>
                        <option>select</option>
                        <option>class1</option>
                        <option>class2</option>
                      </select>
                    </div>
                    <button type="button" className="btn btn-dark" onClick={handleAddQuestions}>+ Add Questions</button>

                    {showQuestions && (
                      <div>
                        {questions.map((question, index) => (
                          <div key={index} className="mb-3">
                            <label htmlFor={`question${index + 1}`} className="form-label">{question}dfghj</label>
                            <div>
                              <label className="form-check-label">
                                <input type="radio" name={`question${index + 1}`} value="option1" className="form-check-input" />
                                Option 1
                              </label>
                              <label className="form-check-label" style={{ marginLeft: "10px" }}>
                                <input type="radio" name={`question${index + 1}`} value="option2" className="form-check-input" />
                                Option 2
                              </label>
                              {/* Add more radio options as needed */}
                            </div>
                          </div>
                        ))}
                      </div>
                    )}

                    <button type="submit" className="btn btn-primary" style={{ marginLeft: "30px" }}>Create</button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default AddExam;
