import React, { useState, useEffect } from "react";
import NavBar from "./Navbar";
import SideBar from "./Sidebar";

const StudentExam = () => {
  const [examData, setExamData] = useState([]);
  const [scheduleExamData, setScheduleExamData] = useState({
    class: "",
    title: "",
    scheduledDate: "",
    scheduledstartTime: "",
    scheduledendTime: "",
  });
  const [isScheduling, setIsScheduling] = useState(false);
  const [selectedExam, setSelectedExam] = useState(null);

  useEffect(() => {
    // Fetch exam data from the backend
    fetch(`http://localhost:4002/api/examview/`)
      .then((response) => response.json())
      .then((data) => {
        setExamData(data);
        console.log("Fetched Exam Data:", data);
      })
      .catch((error) => console.error("Error fetching exam data:", error));
  }, []);

  const handleScheduleExam = (index) => {
    const selectedExam = examData[index];
    setSelectedExam(selectedExam);
    setScheduleExamData({
      class: selectedExam.class,
      title: selectedExam.title,
      scheduledDate: "",
      scheduledstartTime: "",
      scheduledendTime: "",
    });
    setIsScheduling(true);
  };

  const handleScheduleSubmit = (e) => {
    e.preventDefault();

    const { class: examClass, title, scheduledDate, scheduledstartTime, scheduledendTime } = scheduleExamData;

    // Send the scheduled exam data to the backend
    fetch(`http://localhost:4002/api/scheduleexam/${selectedExam._id}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        scheduledDate,
        scheduledstartTime,
        scheduledendTime,
      }),
    })
    .then((response) => response.json())
    .then((data) => {
      alert(data.message);
    })
    .catch((error) => console.error("Error scheduling exam:", error));

    // Reset form data
    setScheduleExamData({
      class: "",
      title: "",
      scheduledDate: "",
      scheduledstartTime: "",
      scheduledendTime: "",
    });

    setIsScheduling(false);
  };

  return (
    <div className="content">
      <NavBar />
      <SideBar />
      <div>
        <h1 className="text-center">·¥áx·¥Ä·¥ç ·¥Ö·¥á·¥õ·¥Ä…™ üÍú±</h1>
        <table border="1" className="table table-hover">
          <thead>
            <tr>
              <th>Class</th>
              <th>Title</th>
              <th>Questions</th>
              <th>Date</th>
              <th>Start Time</th>
              <th>End time</th>
              <th s>Action</th>
            </tr>
          </thead>
          <tbody>
            {examData.map((item, index) => (
              <tr key={index}>
                <td>{item.class}</td>
                <td>{item.title}</td>
                
                <td>
                  <button style={{ marginLeft: '20px', borderRadius: '20px' }}> üëÅÔ∏è‚Äçüó®Ô∏è</button>
                </td>
                <td>{item.scheduledDate}</td>
                <td>{item.scheduledstartTime}</td>
                <td>{item.scheduledendTime}</td>
                <td>
                  <button className="btn btn-info m-2" onClick={() => handleScheduleExam(index)}>
                    START
                  </button>
                  <button className="btn btn-info m-2" onClick={() => handleScheduleExam(index)}>
                    STOP
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

          </div>
        
      </div>
    
  );
};

export default StudentExam;
