import React, { useState, useEffect } from "react";
import SideBar from "./Sidebar";
import NavBar from "./Navbar";

function TeacherHome() {
  const [teacherDetails, setTeacherDetails] = useState({
    name: "",
    subject: "",
    // Add other relevant details here
  });

  // Assuming you have a function to fetch teacher details based on authentication
  const fetchTeacherDetails = async () => {
    try {
      // Fetch teacher details based on the authentication status
      // Replace the following URL with your actual API endpoint
      const response = await fetch('http://localhost:4002/teacher/details');
      if (response.ok) {
        const data = await response.json();
        setTeacherDetails(data);
      } else {
        console.error('Failed to fetch teacher details');
      }
    } catch (error) {
      console.error('Error fetching teacher details:', error);
    }
  };

  // Fetch teacher details when the component mounts
  useEffect(() => {
    fetchTeacherDetails();
  }, []);

  return (
    <>
      <div className="content">
        <SideBar></SideBar>
        <NavBar></NavBar>
        <div className="container-fluid pt-4 px-4">
          <div className="row g-4">
            {/* ... Other Sections ... */}
            <div className="col-sm-12 col-xl-6 alert alert-primary">
              <div className="bg-light rounded h-100 p-4">
                <h4 className="mb-4 text-center">Profile</h4>
                <div className="text-center">
                  <img
                    className="rounded-circle"
                    src="img/user.jpeg"
                    alt=""
                    style={{ width: '100px', height: '100px' }}
                  />
                </div>
                <br></br>
                <p className="text-center">Name: {teacherDetails.name}</p>
                <p className="text-center">Subject: {teacherDetails.subject}</p>
                <a href="/teacherprofile" className="stretched-link"> </a>
              </div>
            </div>
            {/* ... Other Sections ... */}
          </div>
        </div>
      </div>
    </>
  );
}

export default TeacherHome;
