import React, { useState } from "react";
import SideBar from "./Sidebar";
import NavBar from "./Navbar";

function AddExam() {
  const [numQuestions, setNumQuestions] = useState(0);

  const handleSubmit = (event) => {
    event.preventDefault();
    let params = {
      numQuestions:numQuestions,
     
     
  }
  
  fetch('http://localhost:4002/api/exams',{
      method:'post',
      headers:{
          Accept:'application/json',
          'Content-Type':'application/json'
          
      },
      body:JSON.stringify(params)
  }).then((res)=>res.json()).then((result)=>{
    window.location.reload();
      console.log(result);
  })
    // TODO: Send numQuestions to the database or perform other actions
    console.log("Number of questions:", numQuestions);
  };

  const handleAddQuestion = () => {
    setNumQuestions(numQuestions + 1);
  };

  return (
    <>
      <div className="content">
        <SideBar />
        <NavBar />
        <div style={{ display: 'flex', flexDirection: 'column', marginLeft:'300px', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', padding: '20px' }}>
          <div className="container-fluid pt-4 px-4 ">
            <div className="row g-4 ">
              <div className="col-sm-12 col-xl-6 alert alert-primary">
                <div className="bg-light rounded h-100 p-4">
                  <h4 className="mb-4 text-center">Exam Details</h4>
                  <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                      
                      <label htmlFor="numQuestions" className="form-label">Number of Questions:</label>
                      <input
                        type="number"
                        name="numQuestions"
                        value={numQuestions}
                        onChange={(e) => setNumQuestions(parseInt(e.target.value, 10))}
                        className="form-control"
                        id="numQuestions"
                        aria-describedby="numQuestionsHelp"
                      />
                      <small id="numQuestionsHelp" className="form-text text-muted">
                        {/* Click "+ Add Question" to increase the count. */}
                      </small>
                    </div>
                    <button type="button" className="btn btn-dark" onClick={handleAddQuestion}>+ Add Question</button>
                    <button type="submit" className="btn btn-primary" style={{ marginLeft: "30px" }}>Create</button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default AddExam;




