import React, { useState, useEffect } from "react";
import NavBar from "./Navbar";

function ExamResult() {
  const [marks, setMarks] = useState(0);
  const username = localStorage.getItem("username");

  useEffect(() => {
    // Fetch marks data from the backend API
    fetch("http://localhost:4002/getmarks", {
      method: "post",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ username: username }), // Use username instead of id
    })
      .then((res) => res.json())
      .then((result) => {
        setMarks(result.totalMarks); // Use the updated property from the backend
      })
      .catch((error) => {
        console.error("Error fetching marks:", error);
      });
  }, [username]);

