import React, { useState } from "react";
import SideBar from "./Sidebar";
import NavBar from "./Navbar";

function AddExam() {
  const [questions, setQuestions] = useState([]);
  const [questionCounter, setQuestionCounter] = useState(1);

  const handleSubmit = (event) => {
   
    event.preventDefault();
  
  };

  const handleAddQuestion = () => {
    setQuestions([...questions, { id: questionCounter }]);
    setQuestionCounter(questionCounter + 1);
  };

  return (
    <>
      <div className="content">
        <SideBar />
        <NavBar />
        <div style={{ display: 'flex', flexDirection: 'column', marginLeft:'300px', alignItems: 'center', justifyContent: 'center', minHeight: '100vh', padding: '20px' }}>
          <div className="container-fluid pt-4 px-4 ">
            <div className="row g-4 ">
              <div className="col-sm-12 col-xl-6 alert alert-primary">
                <div className="bg-light rounded h-100 p-4">
                  <h4 className="mb-4 text-center">Exam Details</h4>
                  <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                      <label htmlFor="exampleInputEmail1" className="form-label">Title:</label>
                      <input type="text" name="title" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="exampleInputEmail1" className="form-label">Class</label><br></br>
                      <select name="class" className="form-control" style={{ width: '350px' }}>
                        <option>select</option>
                        <option>class1</option>
                        <option>class2</option>
                      </select>
                    </div>
                    {questions.map((question) => (
                      <div key={question.id} className="mb-3">
                        {/* Dynamic set of dropdown questions */}
                        <label htmlFor={`question${question.id}`} className="form-label">Question {question.id}:</label>
                        <select name={`question${question.id}`} className="form-control">
                          <option>Option 1</option>
                          <option>Option 2</option>
                          <option>Option 3</option>
                        </select>
                      </div>
                    ))}
                    <button type="button" className="btn btn-dark" onClick={handleAddQuestion}>+ Add Question</button>
                    <button type="submit" className="btn btn-primary" style={{ marginLeft: "30px" }}>Create</button>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  )
}

export default AddExam;
