			<br/>
			<tr>
				<td>Admission No </td>
				<td>:{value.Admsn}</td>
			</tr>
     <br/>
     
      <br/>
    
      
      
      
     
      
      
 <td><Link to="/studentedit" state={{id:value._id}} style={{ marginLeft: '150px',border:'2px solid',borderRadius:'20px', marginTop:'80px',padding:'8px'}}>Edit</Link></td>
 </>   
 ))}

