import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const ExamView = () => {
  const { examId } = useParams();
  const [examData, setExamData] = useState(null);

  useEffect(() => {
    // Fetch exam data from the backend based on examId
    fetch(`http://localhost:4002/api/examview/`)
      .then((response) => response.json())
      .then((data) => {
        setExamData(data);
        console.log("Fetched Exam Data:", data); // Log the fetched data
      })
      .catch((error) => console.error("Error fetching exam data:", error));
  }, [e]);

  console.log("Exam Data State:", examData); // Log the state

  if (!examData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{examData.title}</h1>
      <p>Class: {examData.class}</p>

      {examData.questions && examData.questions.length > 0 ? (
        <>
          <h2>Questions:</h2>
          <table>
            <thead>
              <tr>
                <th>Question Number</th>
                <th>Question</th>
              </tr>
            </thead>
            <tbody>
              {examData.questions.map((question, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{question}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <p>No questions available for this exam.</p>
      )}
    </div>
  );
};

export default ExamView;
