import React,{useState} from "react";
import NavBar from "./Navbar";
function ExamTest(){
   
    // const[title,setTitle]=useState('')
    const [examData, setExamData] = useState([]);
    const [scheduleExamData, setScheduleExamData] = useState({
      class: "",
      title: "",
      scheduledDate: "",
      scheduledstartTime: "",
      scheduledendTime: "",
    });
    const [isScheduling, setIsScheduling] = useState(false);
    const [selectedExam, setSelectedExam] = useState(null);
  
    useEffect(() => {
      // Fetch exam data from the backend
      fetch(`http://localhost:4002/api/examview/`)
        .then((response) => response.json())
        .then((data) => {
          setExamData(data);
          console.log("Fetched Exam Data:", data);
        })
        .catch((error) => console.error("Error fetching exam data:", error));
    }, []);
    
    return(
        <>
       <NavBar/>
       <form action="" method="post" >
                <div className="mb-3">
                  <label htmlFor="exampleInputEmail1" className="form-label">Title</label>
                  <input type="text"  name='title'className="form-control"  id="exampleInputEmail1" aria-describedby="emailHelp" />
                </div>
                <div className="mb-3">
                  <label htmlFor="exampleInputEmail1" className="form-label">Class</label>
                  <input type="text"  name='class'className="form-control"  id="exampleInputEmail1" aria-describedby="emailHelp" />
                </div>
                <div className="mb-3">
                  <label htmlFor="exampleInputEmail1"className="form-control" >Questions <input type="text"  name='questions'className="form-control"  id="exampleInputEmail1" aria-describedby="emailHelp" /></label>
                  <input type="text"  name='questions'className="form-control"  id="exampleInputEmail1" aria-describedby="emailHelp" />
                 
                </div>
              
                
                
                </form>
        </>
    )
}
export  default ExamTest
