    //       setUser(data.userDetails);
    //       console.log(data)
          
        //   if (data.userDetails.userstatus === "0") {
        //     navigate('/adminhome');
        //   } else if (data.userDetails.userstatus === "1") {
        //     navigate('/teacherhome');
        //   } else if (data.userDetails.userstatus === "2") {
        //     navigate('/studenthome');
        //   }
    //     } else {
    //       if (response.userstatus === 500) {
    //         setErrorMessage('Not a valid user, Please Signup');
    //       } else {
    //         setErrorMessage('Login failed');
    //       }
    //     }
    //   } catch (error) {
    //     console.error('Error during login:', error);
    //     setErrorMessage('Login failed');
    //   }
    // };
  

   
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:4002/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });
    
            if (response.ok) {
                const data = await response.json();
    
                if (data.success) {
                    
                    localStorage.setItem('username', data.username);
    
                           
                    switch (data.redirect) {
                        case '/adminhome':
                            navigate('/adminhome');

                            break;
                        case '/teacherhome':
                            navigate('/teacherhome');
                            break;
                        case '/studenthome':
                            navigate('/studenthome');
                            break;
                        default:
                            setErrorMessage('Invalid user status');
                    }
                    // if (data.redirect) {
                    //     navigate(data.redirect);
                    // } else {
                    //     setErrorMessage('Invalid user status');
                    // }
                } else {
                    setErrorMessage('Invalid email or password');
                }
            } else {
                setErrorMessage('Login failed');
            }
        } catch (error) {
            console.error('Error during login:', error);
            setErrorMessage('Login failed');
        }
    
        //             navigate('/teacherhome');
        //         } else {
        //             setErrorMessage('Invalid email or password');
        //         }
        //     } else {
        //         setErrorMessage('Login failed');
        //     }
        // } catch (error) {
        //     console.error('Error during login:', error);
        //     setErrorMessage('Login failed');
        // }
    };
    
    return(
        <>
      
      <div style={{ backgroundImage:`url(img/bulb.webp)`,width:'5500px',height:'100px',backgroundRepeat:'no-repeat'}}>


     
        <div class="container-fluid">
            <div class="row h-100 align-items-center justify-content-center" style={{ minheight: '100vh'}}>
                <div class="col-12 col-sm-8 col-md-6 col-lg-5 col-xl-4">
                    <div class="bg-light rounded p-4 p-sm-5 my-4 mx-3">
                        <div class="d-flex align-items-center justify-content-between mb-3">
                            <a href="index.html" class="">
                                <h3 className="text-secondary"><i className="fa  me-2"></i>Admin</h3>
                            </a>
                            <h3 className="text-secondary">Sign In</h3>
                        </div>
                        <form action="" method="post" >
                        <div className="form-floating mb-3" >
                            <input type="email" name="email"onChange={(e) => setEmail(e.target.value)}className="form-control"placeholder="name@example.com"/>
                            <label for="floatingInput">Email address</label>
                        </div>
                        <div className="form-floating mb-4">
                            <input type="password" name="password" onChange={(e) => setPassword(e.target.value)}className="form-control"  placeholder="Password"/>
                            <label for="floatingPassword">Password</label>
                        </div>
                        <div className="d-flex align-items-center justify-content-between mb-4">
                            <div className="form-check">
                                <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
                                <label className="form-check-label" for="exampleCheck1">Check me out</label>
                            </div>
                            <a href="">Forgot Password</a>
                        </div>
                        {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}
                        <button type="submit"  className="btn btn-secondary py-3 w-100 mb-4"onClick={handleSubmit}>Sign In</button>
                        <p className="text-center mb-0">Don't have an Account? <a href="/adminsignup">Sign Up</a></p>

                        </form>
                    </div>
                </div>
            </div>
        </div>
        </div>
        </>
    )
}
export default AdminLogin




