import React,{useState,useEffect} from "react";
import { useLocation} from "react-router-dom";
import NavBar from "./Navbar";
function ExamTest(){
    // const [examData, setExamData] = useState([]);
    const[title,setTitle]=useState('')
    const[classs,setClasss]=useState('')
    // const[questions,setQuestions]=useState('')
    const location= useLocation()
    useEffect(()=>{
        let params = {
            id:location.state.id
        }
        c
        fetch('http://localhost:4002/examtest',{
            method:'post',
            headers:{
                Accept:'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify(params)
        }).then((res)=>res.json()).then((result)=>{
            
            setTitle(result.Title)
            setClasss(result.Class)
            // setAddress(result.Address)
            // setPlace(result.Place)
            
        })
        },[])
    return(
        <>
       <NavBar/>
       <form action="" method="post" >
                <div className="mb-3">
                  <label htmlFor="exampleInputEmail1" className="form-label">Title</label>
                  <input type="text"  name='title'className="form-control" onChange={(e)=>setTitle(e.target.value)} value={title} id="exampleInputEmail1" aria-describedby="emailHelp" />
                </div>
                <div className="mb-3">
                  <label htmlFor="exampleInputEmail1" className="form-label">Class</label>
                  <input type="text"  name='class'className="form-control" onChange={(e)=>setClasss(e.target.value)} value={classs} id="exampleInputEmail1" aria-describedby="emailHelp" />
                </div>
                <div className="mb-3">
                  <label htmlFor="exampleInputEmail1" className="form-label">Questions</label>
                  <input type="text"  name='questions'className="form-control"  id="exampleInputEmail1" aria-describedby="emailHelp" />
                </div>
              
                
                
                </form>
        </>
    )
}
export  default ExamTest
