// ExamTest.js
import React, { useState } from "react";
import NavBar from "./Navbar";

const ExamTest = ({ title, questions, onSubmit }) => {
  const [classValue, setClassValue] = useState("");
  // const [userAnswers, setUserAnswers] = useState(Array(questions.length).fill(""));

  const handleAnswerChange = (index, answer) => {
    setUserAnswers((prevAnswers) => {
      const newAnswers = [...prevAnswers];
      newAnswers[index] = answer;
      return newAnswers;
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Additional logic or sending data to backend can be done here
    onSubmit({ title, class: classValue, userAnswers });
  };

  return (
    <>
      <NavBar />
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Title
          </label>
          <input
            type="text"
            name="title"
            className="form-control"
            defaultValue={title}
            readOnly
          />
        </div>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Class
          </label>
          <input
            type="text"
            name="class"
            className="form-control"
            value={classValue}
            onChange={(e) => setClassValue(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">
            Questions
          </label>
          {questions.map((question, index) => (
            <div key={index} className="mb-3">
              <label htmlFor={`question${index + 1}`} className="form-label">
                Question {index + 1}
              </label>
              <input
                type="text"
                name={`question${index + 1}`}
                className="form-control"
                value={question}
                readOnly
              />
              <label htmlFor={`answer${index + 1}`} className="form-label">
                Your Answer
              </label>
              <input
                type="text"
                name={`answer${index + 1}`}
                className="form-control"
                value={userAnswers[index]}
                onChange={(e) => handleAnswerChange(index, e.target.value)}
              />
            </div>
          ))}
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </>
  );
};

export default ExamTest;
