import React, { useState, useEffect } from "react";

function PrevoiusDisplay() {
  const [examDetails, setExamDetails] = useState(null);

  useEffect(() => {
    // Make a request to the backend endpoint to fetch exam details
    fetch("http://localhost:4002//previousexam", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      // You may need to send additional data in the request body if required
    })
      .then((response) => response.json())
      .then((data) => {
        setExamDetails(data);
      })
      .catch((error) => {
        console.error("Error fetching exam details:", error);
      });
  }, []); // Empty dependency array ensures the effect runs only once when component mounts

  return (
    <>
      <h1>Exam</h1>
      {examDetails ? (
        <div>
          {/* Display exam details here based on the structure of the returned data */}
          <p>Exam ID: {examDetails._id}</p>
          {/* Include other details as needed */}
        </div>
      ) : (
        <p>Loading exam details...</p>
      )}
    </>
  );
}

export default PrevoiusDisplay;
