import React,{useEffect,useState} from "react";
import { Link, useNavigate } from 'react-router-dom';
import NavBar from "./Navbar";
function AdminLogin(){
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();
  
    const handleLogin = async () => {
      try {
        const response = await fetch('http://localhost:4000/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ email, pass: password }),
        });
  
        if (response.ok) {
          const data = await response.json();
          setUser(data.userDetails);
          console.log(data)
          
          if (data.userDetails.status === "0") {
            navigate('/adminhome');
          } else if (data.userDetails.status === "1") {
            navigate('/teacherhome');
          } else if (data.userDetails.status === "2") {
            navigate('/studenthom');
          }
        } else {
          if (response.status === 404) {
            setErrorMessage('Not a valid user, Please Signup');
          } else {
            setErrorMessage('Login failed');
          }
        }
      } catch (error) {
        console.error('Error during login:', error);
        setErrorMessage('Login failed');
      }
    };
  
    return(
        <>
      
        <NavBar></NavBar>
     
        <div class="container-fluid">
            <div class="row h-100 align-items-center justify-content-center" style={{ minheight: '100vh'}}>
                <div class="col-12 col-sm-8 col-md-6 col-lg-5 col-xl-4">
                    <div class="bg-light rounded p-4 p-sm-5 my-4 mx-3">
                        <div class="d-flex align-items-center justify-content-between mb-3">
                            <a href="index.html" class="">
                                <h3 class="text-secondary"><i class="fa  me-2"></i>Admin</h3>
                            </a>
                            <h3 class="text-secondary">Sign In</h3>
                        </div>
                        <div class="form-floating mb-3">
                            <input type="email"name="email" class="form-control" id="floatingInput" placeholder="name@example.com"/>
                            <label for="floatingInput">Email address</label>
                        </div>
                        <div class="form-floating mb-4">
                            <input type="password" name="password" class="form-control" id="floatingPassword" placeholder="Password"/>
                            <label for="floatingPassword">Password</label>
                        </div>
                        <div class="d-flex align-items-center justify-content-between mb-4">
                            <div class="form-check">
                                <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
                                <label class="form-check-label" for="exampleCheck1">Check me out</label>
                            </div>
                            <a href="">Forgot Password</a>
                        </div>
                        <button type="submit" class="btn btn-secondary py-3 w-100 mb-4">Sign In</button>
                        <p class="text-center mb-0">Don't have an Account? <a href="/adminsignup">Sign Up</a></p>
                    </div>
                </div>
            </div>
        </div>
        </>
    )
}
export default AdminLogin
